import React from 'react'
import Book from './Book.js'
import * as BooksAPI from './BooksAPI'
import './App.css'
import {Link, Route} from 'react-router-dom';
import BookSearch from './BookSearch.js'
import BookShelves from './BookSearch.js'

class BooksApp extends React.Component {
  state = {
    books: [],
    query: ''
  }

  componentDidMount() {
    BooksAPI.getAll()
    .then((books) => {
      this.setState(() => ({
        books
      }))
    })
  }

  updateBookShelf = (bookInfo, shelfSelection) => {
    BooksAPI.update(bookInfo, shelfSelection)
      .then((book) => { // promise with book return
        bookInfo.shelf = shelfSelection;
        this.setState((currentState) => ({
          books: currentState.books
          .filter(book => book.id !== bookInfo.id)
          .concat([book])
        }))
      })
  }

  updateQuery = (query) => {
    this.setState(() => ({
      query: query
    }))
  }

  render() {
    const {books, query} = this.state
    const bookShelves = [
      {
        id: 'currentlyReading',
        name: 'Currently Reading',
        list: books.filter((book) => (
          book.shelf === 'currentlyReading' ))
      },
      {
        id: 'wantToRead',
        name: 'Want To Read',
        list: books.filter((book) => (
          book.shelf === 'wantToRead' ))
      },
      {
        id: 'read',
        name: 'Read',
        list: books.filter((book) => (
          book.shelf === 'read' ))
      },
      {
        id: 'none',
        name: 'None',
        list: books.filter((book) => (
          book.shelf === 'none' ))
      }
    ]
    const viewableBookShelves =  bookShelves.filter((bookShelf) => (
      bookShelf.id !== 'none' ))

    const queriedBooks = query === ''
    ? []
    : books.filter((book) => (
      book.title.toLowerCase().includes(query.toLowerCase())
    ))

    return (
      <div className="app">

        <Route path='/search' render={({history}) => (
          <BookSearch queriedBooks={queriedBooks} query={this.state.query} updateQuery={this.updateQuery} bookShelves={bookShelves}/>
        )} />

        <Route exact path='/' render={() => (
          <div className="list-books">
            <div className="list-books-title">
              <h1>MyReads</h1>
            </div>
            <BookShelves viewableBookShelves={viewableBookShelves} updateBookShelf={this.updateBookShelf} />
            <div className="open-search">
              <Link
                to='/search'>
                <button>Add a book</button>
              </Link>
            </div>
          </div>
        )} />
      </div>
    )
  }
}

export default BooksApp
