import React from 'react'
// import * as BooksAPI from './BooksAPI'
import './App.css'
import PropTypes from 'prop-types'
import * as BooksAPI from './BooksAPI'

class Move extends React.Component {
  static propTypes = {
    bookInfo: PropTypes.object.isRequired
  }

  updateBookShelf = (shelfSelection) => {
    BooksAPI.update(this.props.bookInfo, shelfSelection)
      .then((book) => { // promise with contract return
        this.setState((currentState) => ({
          contacts: currentState.books.concat([book])
        }))
      })
  }

  render () {
    const {bookInfo, bookShelves} = this.props

    return (
      <div key={bookInfo.title} className="book-shelf-changer">
        <select onChange={(event) => this.updateBookShelf(event.target.value)}>
          <option value="move" disabled>Move to...</option>
          {bookShelves.map((bookShelf) => (
            <option key={bookShelf.id} value={bookShelf.id}>{bookShelf.name}</option>
          ))}
        </select>
      </div>
    )
  }

}

export default Move
