import React from 'react'
import './App.css'
import PropTypes from 'prop-types'
// import * as BooksAPI from './BooksAPI'

class Move extends React.Component {
  static propTypes = {
    bookInfo: PropTypes.object.isRequired
  }

  updateBookShelf = (bookInfo, shelfSelection) => {
    if (this.props.onUpdateBookShelf) {
      console.log(bookInfo, shelfSelection);
      this.props.onUpdateBookShelf(this.props.bookInfo, shelfSelection)
    }
    // BooksAPI.update(bookInfo, shelfSelection).then(() => this.getBooks());
  }

//   handleBookShelf(book, shelf) {
//     BooksAPI.update(book, shelf).then(() => this.getBooks());
// }

  render () {
    const {bookInfo, bookShelves, onUpdateBookShelf} = this.props
    return (
      <div key={bookInfo.title} className="book-shelf-changer">
        <select onChange={(event) => this.updateBookShelf(bookInfo, event.target.value)} defaultValue={bookInfo.shelf ? bookInfo.shelf : 'none'}>
          <option value="move" disabled>Move to...</option>
          {bookShelves.map((bookShelf) => (
            <option key={bookShelf.id} value={bookShelf.id}>{bookShelf.name}</option>
          ))}
        </select>
      </div>
    )
  }

}

export default Move
